(448, 'Analisis Kinerja Perdagangan Kentang Semester II Tahun 2022', 'publikasi', 'Volume 12 Nomor 2F Tahun 2022', 'ISSN: 2086-4949', '2022-12-30', '1.22', 57, '<div style="text-align: justify;"><span style="font-size: 1rem;">Publikasi Analisis Kinerja Perdagangan Kentang Tahun 2022 merupakan bagian dari publikasi Kinerj Perdagangan Komoditas Pertanian tahun 2022. Analisis yang disajikan berupa keragaan data series komoditas kentang secara nasional dan internasional selama 5 tahun terakhir serta dilengkapi dengan hasil analisis indeks spesialisasi perdagangan, analisis daya saing, indeks keunggulan komparatif, penetrasi pasar dan analisis lainnya.</span></div>', 'Analisis_Kinerja_Perdagangan_Kentang_2022.pdf', '0100000000', '<div style="text-align: justify;"><span style="font-size: 1rem;">Produksi kentang di Indonesia mencapai 1,36 juta ton pada 2021. Produksi kentang mengalami peningkatan 6,1% dari tahun sebelumnya sebesar 1,28 juta ton. Provinsi sentra produksi kentang di dominasi Jawa Timur, Jawa Tengah dan Jawa Barat yang masing-masing memberikan kontribusi sebesar 23,83%, 20,40% dan 17,67%.</span></div>', 'Analisis_Kinerja_Perdagangan_Kentang_2022_001.jpg', 1, '2023-08-21 13:17:19'),
(348, 'Outlook Karet 2021', 'publikasi', '-', 'ISSN : 1907-1507', '2022-04-01', '4,598', 138, '<p><div style="text-align: justify;"><span style="font-size: 1rem;">Publikasi Outlook Karet Tahun 2021 sebagai bagian dari Outlook Komoditas</span></div><span class="fontstyle0"><div style="text-align: justify;"><span style="font-size: 1rem;">Perkebunan menyajikan keragaan data series komoditi karet secara nasional dan</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">internasional selama lima sampai sepuluh tahun terakhir serta dilengkapi dengan</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">hasil analisis proyeksi produksi dan ketersediaan dari tahun 2021 sampai dengan</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">tahun 2025</span></div></span></p>', 'Outlook_Karet_ttd_Kapus_1_April_2022.pdf', '0100000000', '<p><div style="text-align: justify;"><span style="font-size: 1rem;">Perkembangan karet di Indonesia untuk luas areal cenderung sedikit</span></div><span class="fontstyle0"><div style="text-align: justify;"><span style="font-size: 1rem;">meningkat setiap tahunnya sejak tahun 2012 hingga 2021, tetapi produksinya</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">cenderung turun sejak tahun 2018 sampai 2020. Tanaman karet di Indonesia</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">sebagian besar dikuasai oleh Perkebunan Rakyat yaitu sekitar 83,58%, sisanya</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">6,87% dikuasai Perkebunan Negara, dan 9,55% dikuasai Perkebunan Swasta. Pada</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">tahun 2021 menurut angka estimasi Ditjenbun luas areal karet nasional meningkat</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">0,30% dari tahun 2020, atau meningkat dari 3,681 juta hektar menjadi 3,692 juta</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">hektar, sementara angka produksi karet meningkat 8,21% dari 2,88 juta ton tahun</span></div><div style="text-align: justify;"><span style="font-size: 1rem;">2020, menjadi 3,12 juta ton tahun 2021</span></div></span></p>', 'Outlook_Karet_2021.png', 1, '2023-01-16 15:34:12'),
(411, 'Buletin Konsumsi Pangan Volume 13 Nomor 1 Tahun 2022', 'publikasi', 'Volume 13 Nomor 1 Tahun 2022', '-', '2022-05-01', '2.87', 112, '<p><font face="Calibri, sans-serif">Penyajian buku Analisis Ketahanan Pangan meliputi keragaan penyusun aspek ketahanan pangan diantaranya pola panen dan produksi padi serta provinsi sentra produksi komoditas pangan, stok pangan, konsumsi, pengeluaran untuk konsumsi, kemiskinan, ketidakcukupan pangan dan indikator lainnya selama 3 sampai 5 tahun terakhir serta dilengkapi dengan analisis <i>import dependency ratio</i> (IDR) dan&nbsp; <i>self sufficiency ratio</i> (SSR) komoditas pangan, analisis neraca&nbsp; penyediaan dan kebutuhan pangan, serta analisis ketahanan pangan lainnya.</font><br></p>', 'Buletin_Konsumsi_Pangan_Vol13_No_1_Tahun_2022.pdf', '0100000000', '<p><span style="font-family: Calibri, sans-serif;">Penyajian buku Analisis Ketahanan Pangan meliputi keragaan penyusun aspek ketahanan pangan diantaranya pola panen dan produksi padi serta provinsi sentra produksi komoditas pangan, stok pangan, konsumsi, pengeluaran untuk konsumsi, kemiskinan, ketidakcukupan pangan dan indikator lainnya selama 3 sampai 5 tahun terakhir serta dilengkapi dengan analisis&nbsp;</span><i style="font-family: Calibri, sans-serif;">import dependency ratio</i><span style="font-family: Calibri, sans-serif;">&nbsp;(IDR) dan&nbsp;&nbsp;</span><i style="font-family: Calibri, sans-serif;">self sufficiency ratio</i><span style="font-family: Calibri, sans-serif;">&nbsp;(SSR) komoditas pangan, analisis neraca&nbsp; penyediaan dan kebutuhan pangan, serta analisis ketahanan pangan lainnya.</span><br></p>', 'Buletin_Konsumsi_Pangan_Vol13_No_1_Tahun_2022.jpg', 1, '2023-03-01 08:48:28'),
(410, 'Analisis Ketahanan Pangan Tahun 2022', 'publikasi', '-', '-', '2022-11-01', '2.05', 101, '<p><span lang="EN" style="font-size:12.0pt;line-height:\r\n107%;font-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;\r\nmso-fareast-font-family:Calibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:\r\nminor-latin;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-bidi-theme-font:minor-bidi;\r\nmso-ansi-language:EN;mso-fareast-language:EN-US;mso-bidi-language:AR-SA">Penyajian\r\nbuku Analisis Ketahanan Pangan meliputi keragaan penyusun aspek ketahanan\r\npangan diantaranya pola panen dan produksi padi serta provinsi sentra produksi komoditas\r\npangan, stok pangan, konsumsi, pengeluaran untuk konsumsi, kemiskinan,\r\nketidakcukupan pangan dan indikator lainnya selama 3 sampai 5 tahun terakhir\r\nserta dilengkapi dengan analisis <i>import dependency ratio</i> (IDR) dan&nbsp; <i>self sufficiency ratio</i> (SSR) komoditas\r\npangan, analisis neraca&nbsp; penyediaan dan\r\nkebutuhan pangan, serta analisis ketahanan pangan lainnya.</span><br></p>', 'Analisis_Ketahanan_Pangan_Tahun_2022.pdf', '0100000000', '<p><span lang="EN" style="font-size:12.0pt;line-height:\r\n107%;font-family:&quot;Calibri&quot;,sans-serif;mso-ascii-theme-font:minor-latin;\r\nmso-fareast-font-family:Calibri;mso-fareast-theme-font:minor-latin;mso-hansi-theme-font:\r\nminor-latin;mso-bidi-font-family:&quot;Times New Roman&quot;;mso-bidi-theme-font:minor-bidi;\r\nmso-ansi-language:EN;mso-fareast-language:EN-US;mso-bidi-language:AR-SA">Penyajian\r\nbuku Analisis Ketahanan Pangan meliputi keragaan penyusun aspek ketahanan\r\npangan diantaranya pola panen dan produksi padi serta provinsi sentra produksi komoditas\r\npangan, stok pangan, konsumsi, pengeluaran untuk konsumsi, kemiskinan,\r\nketidakcukupan pangan dan indikator lainnya selama 3 sampai 5 tahun terakhir\r\nserta dilengkapi dengan analisis <i>import dependency ratio</i> (IDR) dan&nbsp; <i>self sufficiency ratio</i> (SSR) komoditas\r\npangan, analisis neraca&nbsp; penyediaan dan\r\nkebutuhan pangan, serta analisis ketahanan pangan lainnya.</span><br></p>', 'Analisis_Ketahanan_Pangan_Tahun_2022.jpg', 1, '2023-02-28 11:01:48');
